import React, { useState } from 'react';
import './StatisticsModal.css';

const StatisticsModal = ({ onClose }) => {
  const [currentMonth, setCurrentMonth] = useState('Окт');
  
  const months = ['Янв', 'Фев', 'Мар', 'Апр', 'Май', 'Июн', 'Июл', 'Авг', 'Сен', 'Окт', 'Ноя', 'Дек'];
  
  const currentMonthIndex = months.indexOf(currentMonth);
  
  const handlePrevMonth = () => {
    const prevIndex = (currentMonthIndex - 1 + months.length) % months.length;
    setCurrentMonth(months[prevIndex]);
  };
  
  const handleNextMonth = () => {
    const nextIndex = (currentMonthIndex + 1) % months.length;
    setCurrentMonth(months[nextIndex]);
  };

  // Моковые данные для графика
  const chartData = [
    { day: '1', value: 120 },
    { day: '2', value: 90 },
    { day: '3', value: 150 },
    { day: '4', value: 80 },
    { day: '5', value: 200 },
    { day: '6', value: 60 },
    { day: '7', value: 180 }
  ];

  const maxValue = Math.max(...chartData.map(item => item.value));

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="statistics-modal" onClick={(e) => e.stopPropagation()}>
        {/* Заголовок */}
        <div className="modal-header">
          <h2>Статистика</h2>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>

        {/* Блок "Сегодня" */}
        <div className="today-section">
          <div className="today-title">Сегодня</div>
          <div className="total-time">
            <div className="total-label">Всего</div>
            <div className="time-value">2ч 30м</div>
          </div>
        </div>

        {/* Переключение месяцев */}
        <div className="month-selector">
          <button className="month-arrow" onClick={handlePrevMonth}>◀</button>
          <span className="current-month">{currentMonth}</span>
          <button className="month-arrow" onClick={handleNextMonth}>▶</button>
        </div>

        {/* Столбчатая диаграмма */}
        <div className="chart-container">
          <div className="chart">
            {chartData.map((item, index) => (
              <div key={index} className="chart-bar-container">
                <div 
                  className="chart-bar"
                  style={{ height: `${(item.value / maxValue) * 100}%` }}
                ></div>
                <div className="chart-label">{item.day}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Подпись под графиком */}
        <div className="chart-footer">
          <div className="chart-legend">
            <div className="legend-item">
              <div className="legend-color"></div>
              <span>Минуты фокуса</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StatisticsModal;
