import React, { useState, useEffect } from 'react';
import { timerAPI } from '../services/api';
import './TimerWidget.css';

const TimerWidget = () => {
  const [timer, setTimer] = useState({
    isRunning: false,
    timeLeft: 25 * 60,
    mode: 'work'
  });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchCurrentTimer();
  }, []);

  useEffect(() => {
    let interval;
    if (timer.isRunning && timer.timeLeft > 0) {
      interval = setInterval(() => {
        setTimer(prev => ({
          ...prev,
          timeLeft: prev.timeLeft - 1
        }));
      }, 1000);
    } else if (timer.timeLeft === 0) {
      handleTimerComplete();
    }

    return () => clearInterval(interval);
  }, [timer.isRunning, timer.timeLeft]);

  const fetchCurrentTimer = async () => {
    try {
      const response = await timerAPI.getCurrent();
      if (response.data) {
        setTimer(response.data);
      }
    } catch (error) {
      console.log('Таймер не найден, используем значения по умолчанию');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handleStart = async () => {
    setIsLoading(true);
    try {
      await timerAPI.start();
      setTimer(prev => ({
        ...prev,
        isRunning: true
      }));
    } catch (error) {
      console.error('Ошибка при запуске таймера:', error);
      setTimer(prev => ({
        ...prev,
        isRunning: true
      }));
    } finally {
      setIsLoading(false);
    }
  };

  const handleStop = async () => {
    setIsLoading(true);
    try {
      await timerAPI.stop();
      setTimer(prev => ({
        ...prev,
        isRunning: false
      }));
    } catch (error) {
      console.error('Ошибка при остановке таймера:', error);
      setTimer(prev => ({
        ...prev,
        isRunning: false
      }));
    } finally {
      setIsLoading(false);
    }
  };

  const handleTimerComplete = () => {
    setTimer(prev => ({
      ...prev,
      isRunning: false,
      mode: prev.mode === 'work' ? 'break' : 'work',
      timeLeft: prev.mode === 'work' ? 5 * 60 : 25 * 60
    }));
  };

  return (
    <div className="timer-widget">
      <div className="timer-display">
        <div className="timer-time">{formatTime(timer.timeLeft)}</div>
        <div className="timer-mode">
          {timer.mode === 'work' ? 'Работа' : 'Перерыв'}
        </div>
      </div>
      
      <div className="timer-controls">
        <button
          onClick={handleStart}
          disabled={timer.isRunning || isLoading}
          className="timer-btn start-btn"
        >
          Старт
        </button>
        
        <button
          onClick={handleStop}
          disabled={!timer.isRunning || isLoading}
          className="timer-btn stop-btn"
        >
          Стоп
        </button>
      </div>
    </div>
  );
};

export default TimerWidget;
