import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './AuthForms.css';

const Login = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    email: '',
    code: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useAuth();

  useEffect(() => {
    if (location.state?.email) {
      setFormData(prev => ({ ...prev, email: location.state.email }));
      setStep(2);
    }
    if (location.state?.message) {
      setMessage(location.state.message);
    }
  }, [location]);

  const validateEmail = () => {
    if (!formData.email) {
      setErrors({ email: 'Email обязателен' });
      return false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      setErrors({ email: 'Некорректный формат email' });
      return false;
    }
    setErrors({});
    return true;
  };

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateEmail()) return;

    setIsLoading(true);
    try {
      await axios.post('/api/auth/login', { email: formData.email });
      setStep(2);
      setMessage('Код подтверждения отправлен на вашу почту');
      setErrors({});
    } catch (error) {
      setErrors({ 
        submit: 'Произошла ошибка при отправке кода. Попробуйте еще раз.' 
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCodeSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.code) {
      setErrors({ code: 'Код обязателен' });
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.post('/api/auth/verify', {
        email: formData.email,
        code: formData.code
      });
      
      if (response.data.token && response.data.user) {
        login(response.data.token, response.data.user);
        navigate('/');
      }
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setErrors({ code: 'Неверный код подтверждения' });
      } else {
        setErrors({ 
          submit: 'Произошла ошибка при входе. Попробуйте еще раз.' 
        });
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>Вход</h2>
        
        {message && <div className="success-message">{message}</div>}

        {step === 1 ? (
          <form onSubmit={handleEmailSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={errors.email ? 'error' : ''}
                placeholder="Введите ваш email"
              />
              {errors.email && <span className="error-text">{errors.email}</span>}
            </div>

            {errors.submit && <div className="error-text submit-error">{errors.submit}</div>}

            <button 
              type="submit" 
              className="submit-btn"
              disabled={isLoading}
            >
              {isLoading ? 'Отправка...' : 'Получить код'}
            </button>
          </form>
        ) : (
          <form onSubmit={handleCodeSubmit}>
            <div className="form-group">
              <label htmlFor="code">Код подтверждения</label>
              <input
                type="text"
                id="code"
                name="code"
                value={formData.code}
                onChange={handleChange}
                className={errors.code ? 'error' : ''}
                placeholder="Введите код из письма"
              />
              {errors.code && <span className="error-text">{errors.code}</span>}
            </div>

            {errors.submit && <div className="error-text submit-error">{errors.submit}</div>}

            <button 
              type="submit" 
              className="submit-btn"
              disabled={isLoading}
            >
              {isLoading ? 'Вход...' : 'Войти'}
            </button>

            <button 
              type="button" 
              className="secondary-btn"
              onClick={() => setStep(1)}
              disabled={isLoading}
            >
              Изменить email
            </button>
          </form>
        )}

        <div className="auth-link">
          Нет аккаунта? <Link to="/register">Зарегистрироваться</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
