import React, { useState } from 'react';
import TimerWidget from './TimerWidget';
import StatisticsModal from './StatisticsModal';
import './HomePage.css';

// Импортируем логотип
import logo from '../assets/images/logo.png'; // или .svg, .jpg

const HomePage = () => {
  const [showStatistics, setShowStatistics] = useState(false);

  return (
    <div className="home-page">
      {/* Логотип */}
      <div className="logo">
        <img src={logo} alt="Pomodoro Timer" className="logo-image" />
      </div>

      {/* Контейнер для кнопок навигации - занимает пространство между лого и таймером */}
      <div className="nav-container">
        <nav className="top-nav">
          <button className="nav-btn">Настройки</button>
          <button 
            className="nav-btn"
            onClick={() => setShowStatistics(true)}
          >
            Статистика
          </button>
          <button className="nav-btn">Профиль</button>
        </nav>
      </div>

      {/* Основной контент */}
      <div className="main-content">
        <TimerWidget />
      </div>

      {/* Модальное окно статистики */}
      {showStatistics && (
        <StatisticsModal onClose={() => setShowStatistics(false)} />
      )}
    </div>
  );
};

export default HomePage;
