import React, { useState, useEffect } from 'react';
import { statisticsAPI } from '../services/api';
import './Statistics.css';

const Statistics = () => {
  const [statistics, setStatistics] = useState([]);
  const [period, setPeriod] = useState({
    from: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    to: new Date().toISOString().split('T')[0]
  });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchStatistics();
  }, [period]);

  const fetchStatistics = async () => {
    setIsLoading(true);
    try {
      const response = await statisticsAPI.get(period);
      setStatistics(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке статистики:', error);
      setStatistics([]);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePeriodChange = (e) => {
    const { name, value } = e.target;
    setPeriod(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const totalPomodoros = statistics.reduce((sum, day) => sum + (day.completedPomodoros || 0), 0);
  const totalFocusTime = statistics.reduce((sum, day) => sum + (day.focusTime || 0), 0);

  return (
    <div className="statistics">
      <h2>Статистика</h2>
      
      <div className="period-selector">
        <div className="period-inputs">
          <div className="input-group">
            <label htmlFor="from">С:</label>
            <input
              type="date"
              id="from"
              name="from"
              value={period.from}
              onChange={handlePeriodChange}
            />
          </div>
          
          <div className="input-group">
            <label htmlFor="to">По:</label>
            <input
              type="date"
              id="to"
              name="to"
              value={period.to}
              onChange={handlePeriodChange}
            />
          </div>
        </div>
      </div>

      {isLoading ? (
        <div className="loading">Загрузка статистики...</div>
      ) : (
        <>
          <div className="stats-summary">
            <div className="stat-card">
              <h3>Всего помидоров</h3>
              <div className="stat-value">{totalPomodoros}</div>
            </div>
            <div className="stat-card">
              <h3>Общее время фокуса</h3>
              <div className="stat-value">
                {Math.floor(totalFocusTime / 60)}ч {totalFocusTime % 60}м
              </div>
            </div>
          </div>

          <div className="stats-details">
            <h3>Детальная статистика</h3>
            {statistics.length === 0 ? (
              <div className="no-data">Нет данных за выбранный период</div>
            ) : (
              <div className="stats-list">
                {statistics.map((day, index) => (
                  <div key={index} className="stats-day">
                    <div className="day-date">{new Date(day.date).toLocaleDateString('ru-RU')}</div>
                    <div className="day-stats">
                      <span>Помидоры: {day.completedPomodoros || 0}</span>
                      <span>Время: {Math.floor((day.focusTime || 0) / 60)}ч {(day.focusTime || 0) % 60}м</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Statistics;
